import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  Button,
  TextInput,
} from "react-native";
import { Feather } from "@expo/vector-icons";
import Menu from "./menu";

export default function InformasiScreen({ navigation }) {
  return (
    <View style={styles.container}>
      <View style={styles.kotakFrame}>
        <View style={styles.header}>
          <Text style={styles.text}>Kapalzy</Text>
        </View>
        <View style={styles.kotakDetail}>
          <ScrollView>
            <Text style={styles.textJudulRincian}>Informasi Pemesanan</Text>
            <View style={styles.kotakRincian}>
              <View style={styles.bandaraAndIcon}>
                <Text>Bakauheni</Text>
                <Feather
                  name="arrow-right"
                  size={30}
                  style={{ marginLeft: 20, marginRight: 20 }}
                ></Feather>
                <Text>Merak</Text>
              </View>
              <Text style={styles.textBold}>Jadwal Masuk Pelabuhan</Text>
              <Text style={styles.textBiasa}>Kamis, 17 Maret 2022</Text>
              <Text style={styles.textBiasa}>15.30 WIB</Text>
              <Text style={styles.textBold}>Layanan</Text>
              <Text style={styles.textBiasa}>Express</Text>
              <Text numberOfLines={1}>
                _______________________________________
              </Text>
              <View style={styles.areaTotal}>
                <Text style={styles.textAreaTotal}>Dewasa x 1</Text>
                <Text style={styles.textAreaTotal}>Rp. 65.000</Text>
              </View>
            </View>
            <Text style={styles.textJudulRincian}>Data Pemesan</Text>
            <Text style={styles.textJudulRincian}>Nama Lengkap</Text>
            <TextInput style={styles.textInput} />
            <Text style={styles.textJudulRincian}>Identitas</Text>
            <TextInput style={styles.textInput} />
            <Text style={styles.textJudulRincian}>Umur</Text>
            <TextInput style={styles.textInputUmur} />
            <Button
              title="Submit"
              onPress={() => {
                alert("Berhasil");
              }}
            ></Button>
          </ScrollView>
        </View>
      </View>

      {Menu({ navigation })}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#d3d3d3",
    alignItems: "center",
    justifyContent: "center",
  },
  kotakFrame: {
    height: 580,
    marginBottom: 20,
    width: 330,
    elevation: 2,
    borderWidth: 0.5,
    borderColor: "#DCDCDC",
    backgroundColor: "#FFF",
  },
  text: {
    fontSize: 25,
    fontWeight: "bold",
    color: "blue",
  },
  header: {
    flex: 0.13,
    width: "auto",
    justifyContent: "center",
    alignItems: "center",
  },

  kotakDetail: {
    width: "auto",
    flex: 0.87,
    padding: 20,
  },
  kotakRincian: {
    backgroundColor: "#e9e9e9",
    padding: 20,
    borderRadius: 7,
  },
  textJudulRincian: {
    fontWeight: "bold",
    fontSize: 16,
    marginTop: 5,
    marginBottom: 10,
  },

  bandaraAndIcon: {
    backgroundColor: "#808080",
    flexDirection: "row",
    alignItems: "center",
    padding: 5,
    borderRadius: 5,
    justifyContent: "space-around",
  },
  textBold: {
    fontWeight: "bold",
    fontSize: 14,
    marginTop: 5,
    marginBottom: 10,
  },
  textBiasa: {
    marginLeft: 10,
  },
  areaTotal: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 10,
    marginBottom: 20,
  },
  textAreaTotal: {
    fontWeight: "bold",
    fontSize: 14,
  },
  textInput: {
    backgroundColor: "#e9e9e9",
    fontSize: 16,
    borderRadius: 7,
    width: "auto",
    marginLeft: 5,
    padding: 5,
    paddingLeft: 10,
  },
  textInputUmur: {
    marginBottom: 20,
    backgroundColor: "#e9e9e9",
    fontSize: 16,
    borderRadius: 7,
    width: "auto",
    marginLeft: 5,
    padding: 5,
    paddingLeft: 10,
  },
});
