import { StyleSheet, Text, View, TextInput } from "react-native";
import Icons from "@expo/vector-icons/Ionicons";
import { AntDesign, MaterialIcons, Ionicons } from "@expo/vector-icons";
import FontAwesome5 from "@expo/vector-icons/FontAwesome5";
import Menu from "./menu";
import React, { useState } from "react";
import { Alert } from "react-native-web";

export default function BerandaScreen({ navigation }) {
  const [pelabuhanAwal, setPelabuhanAwal] = useState("");
  const [pelabuhanTujuan, setPelabuhanTujuan] = useState("");
  const [layanan, setLayanan] = useState("");
  const [tgl, setTgl] = useState("");
  const [jam, setJam] = useState("");
  const [kategori, setKategori] = useState("");

  const data = {
    pelabuhanAwal: pelabuhanAwal,
    pelabuhanTujuan: pelabuhanTujuan,
    layanan: layanan,
    tgl: tgl,
    jam: jam,
    kategori: kategori,
  };

  return (
    <View style={styles.container}>
      <View style={styles.kotakFrame}>
        <View style={styles.header}>
          <Text style={styles.text}>Kapalzy</Text>
        </View>
        <View style={styles.kotak}>
          <View style={styles.kotakInput}>
            <Text>Pelabuhan Awal</Text>
            <View style={styles.areaIconAndInput}>
              <Ionicons name="md-boat-outline" size={27} />
              <TextInput
                placeholder="Masukkan Nama Pelabuhan"
                onChangeText={(val) => setPelabuhanAwal(val)}
                style={styles.textInput}
              />
            </View>
          </View>
          <View style={styles.kotakInput}>
            <Text>Pelabuhan Tujuan</Text>
            <View style={styles.areaIconAndInput}>
              <Ionicons name="md-boat" size={27} />
              <TextInput
                placeholder="Masukkan Tujuan Pelabuhan"
                onChangeText={(val) => setPelabuhanTujuan(val)}
                style={styles.textInput}
              />
            </View>
          </View>
          <View style={styles.kotakInput}>
            <Text>Kelas Layanan</Text>
            <View style={styles.areaIconAndInput}>
              <MaterialIcons name="airline-seat-recline-normal" size={27} />
              <TextInput
                placeholder="Pilih Layanan"
                onChangeText={(val) => setLayanan(val)}
                style={styles.textInput}
              />
            </View>
          </View>
          <View style={styles.kotakInput}>
            <Text>Tanggal Masuk Pelabuhan</Text>
            <View style={styles.areaIconAndInput}>
              <Icons name="calendar" size={27} />
              <TextInput
                placeholder="Tanggal Keberangkatan"
                onChangeText={(val) => setTgl(val)}
                style={styles.textInput}
              />
            </View>
          </View>
          <View style={styles.kotakInput}>
            <Text>Jam Masuk Pelabuhan</Text>
            <View style={styles.areaIconAndInput}>
              <AntDesign name="clockcircle" size={27} />
              <TextInput
                placeholder="Jam Keberangkatan"
                onChangeText={(val) => setJam(val)}
                style={styles.textInput}
              />
            </View>
          </View>
          <View style={styles.kategoriPengunjung}>
            <Text>Dewasa</Text>
          </View>

          <FontAwesome5.Button
            name="plus"
            backgroundColor="#FF8C00"
            onPress={() => {
              if (pelabuhanAwal != "" && pelabuhanTujuan != "") {
                navigation.navigate("Rincian", { data });
              } else {
                alert("Isikan Semua Data");
              }
            }}
            style={{ width: 150, justifyContent: "center" }}
          >
            Buat Tiket
          </FontAwesome5.Button>
        </View>
      </View>

      {Menu({ navigation })}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#d3d3d3",
    alignItems: "center",
    justifyContent: "center",
  },
  kotakFrame: {
    backgroundColor: "#FFF",
    height: 580,
    marginBottom: 20,
    width: 330,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 1.41,
    elevation: 2,
  },
  text: {
    fontSize: 25,
    fontWeight: "bold",
    color: "blue",
  },
  header: {
    flex: 0.13,
    width: "auto",
    justifyContent: "center",
    alignItems: "center",
  },
  kotak: {
    width: "auto",
    flex: 0.85,
    alignItems: "center",
  },
  kotakInput: {
    width: "auto",
    height: "auto",
    backgroundColor: "#fff",
    padding: 10,
    paddingTop: 2,
    paddingBottom: 15,
  },
  textInput: {
    backgroundColor: "#e9e9e9",
    fontSize: 16,
    borderRadius: 7,
    width: 230,
    marginLeft: 5,
    paddingLeft: 10,
  },
  areaIconAndInput: {
    width: "auto",
    height: "auto",
    flexDirection: "row",
    padding: 2,
  },
  kategoriPengunjung: {
    width: "auto",
    height: "auto",
    backgroundColor: "#e9e9e9",
    padding: 8,
    paddingRight: 200,
    marginTop: 30,
    marginBottom: 20,
    borderRadius: 6,
  },
});
